package 老师0713;

public class 类与对象 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         Person p;//创建对象
         Person per = new Person();//创建对象并初始化    赋初值
         per.name="张三";
         per.age=19;
         
         System.out.println(per.name);
         System.out.println(per.age);
         
         /*
          * 为对象的属性赋值的方式：
          * 1.对象名.属性名
          * 2.
          * 3.
          */
	}

}
