package 老师0726;

public class Math类 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       System.out.println(Math.random());//[0~1)
       System.out.println(Math.PI);
       System.out.println(Math.abs(-12.3567));//取绝对值
       System.out.println(Math.ceil(12.345));//向上取整
       System.out.println(Math.floor(12.345));//向下取整
       System.out.println(Math.round(12.345));//四舍五入取整
       System.out.println(Math.pow(2.5, -3.1));//a的b次方
       System.out.println(Math.sqrt(16));//正平方根
       
       System.out.println(Math.max(3.4, 2));
       System.out.println(Math.min(3, 6));
       
	}

}
